mapper.insert:
insert into app_picture (picture_id, viproom_id, picture_url, 
  click_url, priority, dtflag
  )
values (#{pictureId,jdbcType=INTEGER}, #{viproomId,jdbcType=VARCHAR}, #{pictureUrl,jdbcType=VARCHAR}, 
  #{clickUrl,jdbcType=VARCHAR}, #{priority,jdbcType=INTEGER}, #{dtflag,jdbcType=INTEGER}
  )

mapper.select:
select

from app_picture


  AND viproom_id = #{viproomId,jdbcType=VARCHAR}


  AND picture_url = #{pictureUrl,jdbcType=VARCHAR}


  AND click_url = #{clickUrl,jdbcType=VARCHAR}


  AND priority = #{priority,jdbcType=INTEGER}


  AND dtflag = #{dtflag,jdbcType=INTEGER}


    AND imagetype = #{imagetype,jdbcType=INTEGER}
