mapper.select:

	SELECT
		t1.airport_id,
		t1.airport_name,
		t1.state,
		t1.agent_num,
		ct.`name` AS province,
		t1.city,
		t1.country,
		t1.address,
		t1.longitude,
		t1.latitude,
		t1.dtflag,
		t1.header_name,
		t1.header_phone,
		ct.cid as cid,
		GROUP_CONCAT(t2.order_id) AS orderIds,
		count(t2.order_id) as total
	FROM
		sys_airport t1
	LEFT JOIN app_orders t2 ON t1.airport_id = t2.airport_id and t2.pay_money is not null
	LEFT JOIN sys_city ct ON ct.CID = t1.province


	
		t1.dtflag=#{dtflag}
	
	
	   and	t1.state=#{state}
	
	
		and t1.airport_id =#{id}
	
	
		and airport_name=#{notLikeName}
	
	
		and airport_name like CONCAT('%', #{name}, '%')
	
	
		and province =#{province}
	
	
		and city =#{city}
	
	
		and country =#{country}
	
	
		and address =#{address}
	
	
		and header_name =#{headerName}
	
	
		and header_phone =#{headerPhone}
	

group by t1.airport_id order by t1.updatetime desc

mapper.update:

	UPDATE 
	  sys_airport 
	SET `airport_id` = #{id},
	dtflag =#{dtflag},state = #{state}


	,airport_name = #{name}


	,agent_num = #{agentNum}


	,province = #{province}


	,city = #{city}


	,country = #{country}


	,address = #{address}


	,longitude=#{longitude}


	,latitude = #{latitude}


	, header_name =#{headerName}


	, header_phone =#{headerPhone}


	, updatetime =#{updatedatetime}


	, address =#{address}

WHERE airport_id = #{id}

mapper.select:
	SELECT
		pid,
		cid,
		name
		FROM
	`sys_city`

	
		pid=#{pid}
	
	
		and cid=#{cid}
	
	
		and name=#{name}
