mapper.select:
		select ord.order_id,ord.order_time,ord.order_state,ord.invoice_flag,ord.apply_type,ord.pay_type,
		ap.airport_name,agent.name,aoc.card_id,
		customer.customer_name,customer.customer_phone
		from app_orders ord,sys_airport ap,sys_users agent,app_customer customer,app_order_card aoc
		where ord.sale_no=agent.code and ord.customer_id = customer.customer_id
		and ord.airport_id = ap.airport_id and ord.order_id=aoc.order_id

		
			and DATE_FORMAT(ord.order_time,'%Y-%m-%d')   >=
			#{startTime}
		

		
		
			and DATE_FORMAT(ord.order_time,'%Y-%m-%d')    <= 
			#{endTime}
		

		
		
			and ord.airport_id = #{airportId}
		

		
		
			and ord.order_state = #{orderState}
		
		
		
			and ord.invoice_flag = #{invoiceState}
		
		
		
			
			and concat(customer.customer_name,customer.customer_phone,agent.name,ord.order_id,aoc.card_id) like
			CONCAT('%','${phoneOrUserName}','%' )
		
		
		
			and ord.airport_id in
			
				#{item}
			

		
       order by ord.order_time desc

select ord.order_id,ord.order_time,ord.order_state,ord.invoice_flag,ord.apply_type, ap.airport_name, agent.name, avp.card_no,avp.card_state, customer.customer_name,customer.customer_phone, acb.back_type,acb.money from app_orders ord,sys_airport ap,sys_users agent,app_customer customer,app_card_back acb,app_order_card aoc,app_vipcard avp where ord.sale_no=agent.code and ord.customer_id = customer.customer_id and ord.airport_id = ap.airport_id and ord.order_id = acb.order_id and ord.order_id=aoc.order_id and avp.card_no=aoc.card_id
and acb.back_type = #{backType}
and ord.apply_type = #{applyType}
and ord.order_state = #{orderState}
and concat(customer.customer_name,customer.customer_phone,ord.order_id) like CONCAT('%','${phoneOrUserName}','%' )
and ord.airport_id in
#{item}
order by ord.order_time desc

mapper.update:
update app_orders set order_state=#{orderState}

,apply_type=#{applyType} 

where order_id = #{orderId}

update app_orders set
order_state=#{orderState},pay_user_id=#{payUserId},pay_time=#{payTime},
serial_id=#{tradeNo},pay_type=#{payType} where order_id = #{orderId}

mapper.select:
SELECT * FROM app_orders ao

	
		order_state=#{orderState}
	
	
		and DATE_FORMAT(order_time,'%Y-%m')=#{dateTime}

		select count(*) as 'value', 'TO_PAY' as 'name' from app_orders where order_state=0 and sale_no=#{uno}
union
select count(*) as 'value', 'TO_ACTIVE' as 'name' from app_orders o join app_order_card oc on o.order_id=oc.order_id join app_vipcard v on oc.card_id=v.card_no  where order_state=1 and  v.card_state in (3,4) and sale_no=#{uno}
union
select count(*) as 'value', 'TO_BACK' as 'name' from app_orders where order_state in (2,3) and sale_no=#{uno}

select o.order_id,o.order_state,o.order_time,c.customer_name,c.customer_phone,av.card_state from app_orders o join app_customer c on o.customer_id=c.customer_id join app_order_card aoc on o.order_id=aoc.order_id join app_vipcard av on aoc.card_id=av.card_no WHERE o.sale_no = (#{uno})
and o.order_state in
#{item}
and av.card_state in
#{cardItem}
and concat(c.customer_name,c.customer_phone) like CONCAT('%','${key}','%' )
