mapper.select:
SELECT SUM(a.`order_num`) AS total FROM app_order_archive a 

	
		a.`year`={year} 
	
	
		and  a.`month`=#{month}

SELECT A.*,B.bname,B.total FROM  (SELECT 
	  archie.`province`,SUM(archie.`order_num`) AS order_sum
	FROM
	  `app_order_archive` archie
	 

	
		archie.`year`=#{year} 
	
	
		and  archie.`month`=#{month} 
	

 GROUP BY archie.`province`	 LIMIT 0,3) A
	 INNER JOIN 
	 (SELECT MAX(a.`order_num`) AS total ,a.bname,a.`province` FROM app_order_archive a
	
		a.`year`=#{year} 


		and  a.`month`=#{month} 

 GROUP BY a.`bname`,a.`province` ) B
ON A.province=B.province

SELECT archie.`province`,SUM(archie.`order_num`) as order_num FROM `app_order_archive` archie
archie.`year`=#{year}
and archie.`month`=#{month}
GROUP BY archie.`province`
