mapper.select:
SELECT SUM(a.`order_num`) AS total FROM app_order_archive a 

	
		a.`year`={year} 
	
	
		and  a.`month`=#{month}

SELECT A.*,B.bname,B.total FROM (SELECT archie.`province`,SUM(archie.`order_num`) AS order_sum FROM `app_order_archive` archie
archie.`year`=#{year}
and archie.`month`=#{month}
GROUP BY archie.`province` LIMIT 0,3) A INNER JOIN (SELECT MAX(a.`order_num`) AS total ,a.bname,a.`province` FROM app_order_archive a
a.`year`=#{year}
and a.`month`=#{month}
GROUP BY a.`bname`,a.`province` ) B ON A.province=B.province order by B.total desc

SELECT archie.`province`,SUM(archie.`order_num`) as order_num FROM `app_order_archive` archie
archie.`year`=#{year}
and archie.`month`=#{month}
GROUP BY archie.`province`

		SELECT SUM(a.`order_num`) AS total FROM `app_order_archive` a WHERE
		a.`year`=#{year} and a.`month`=#{month}
		
			and a.`airport_id` in
			
				#{item}

SELECT a.`airport_id`, a.`airportname`, SUM(a.`order_num`) AS order_num, b.bname, b.maxOrder FROM app_order_archive a INNER JOIN ( SELECT max(a.order_num) AS maxOrder, a.bname, a.airport_id FROM app_order_archive a WHERE a.`year` = #{year} AND a. MONTH = #{month}
and a.`airport_id` in
#{item}
GROUP BY a.airport_id, a.bname ) b ON b.airport_id = a.airport_id WHERE a.`year` = #{year} AND a. MONTH = #{month}
and a.`airport_id` in
#{item}
GROUP BY a.`airportname`, a.`airport_id`

SELECT A.airportname, GROUP_CONCAT(A.bname) AS bnames, GROUP_CONCAT(A.order_num) AS order_nums FROM ( SELECT a.`airport_id`, a.`airportname`, a.`bname`, a.`id`, SUM(a.`order_num`) AS order_num FROM app_order_archive a WHERE a.`year` = #{year} AND a. MONTH = #{month}
and a.`airport_id` in
#{item}
GROUP BY a.airport_id, a.`airportname`, a.`bname`, a.id ) A GROUP BY A.airportname
