mapper.select:
SELECT SUM(a.`order_num`) AS total FROM app_order_archive a 

	
		a.`year`={year} 
	
	
		and  a.`month`=#{month} 
	
	
		and a.`airport_id` in
		
			#{item}

SELECT aoa.pid, aoa.province, aoa.bname, A.max_order as max_order, aoa.`year`, aoa.`month`, aoa.`day`, aoa.airport_id, aoa.airportname, sum(aoa.order_num) AS sum_order FROM app_order_archive aoa INNER JOIN ( SELECT ao.pid, max(ao.order_num) AS max_order FROM app_order_archive ao where ao.`year` = #{year} AND ao. MONTH = #{month} GROUP BY ao.pid ) A ON aoa.pid = A.pid AND A.max_order = aoa.order_num
aoa.`year`=#{year}
and aoa.`month`=#{month}
and aoa.`airport_id` in
#{item}
GROUP BY aoa.pid ORDER BY sum_order DESC LIMIT 0,3

	
	SELECT
		archie.pid,archie.`province`,SUM(archie.`order_num`) as order_num
	FROM
	`app_order_archive` archie

	
		archie.`year`=#{year} 
	
	
		and  archie.`month`=#{month} 
	
	
		and archie.`airport_id` in
		
			#{item}
		
	

GROUP BY archie.`province`,archie.pid

SELECT CASE WHEN COUNT(*) IS NULL THEN 0 ELSE COUNT(*) END AS total FROM `app_orders` ao WHERE ao.`order_state` IN (1,2,3,4)
and ao.`airport_id` in
#{item}

SELECT a.`airport_id`, a.`airportname`, CASE WHEN SUM(a.`order_num`) is null then 0 ELSE SUM(a.`order_num`) end AS order_num, b.bname, b.maxOrder FROM app_order_archive a INNER JOIN ( SELECT max(a.order_num) AS maxOrder, a.bname, a.airport_id FROM app_order_archive a WHERE a.`year` = #{year} AND a. MONTH = #{month} GROUP BY a.airport_id) b ON b.airport_id = a.airport_id and b.maxOrder=a.order_num WHERE a.`year` = #{year} AND a. MONTH = #{month}
and a.`airport_id` in
#{item}
GROUP BY a.`airportname`, a.`airport_id`

SELECT A.airportname, GROUP_CONCAT(A.bname) AS bnames, GROUP_CONCAT(A.order_num) AS order_nums FROM ( SELECT a.`airport_id`, a.`airportname`, a.`bname`, a.bid, CASE WHEN SUM(a.`order_num`) is null THEN 0 ELSE SUM(a.`order_num`) END AS order_num FROM app_order_archive a WHERE a.`year` = #{year} AND a. MONTH = #{month}
and a.`airport_id` in
#{item}
GROUP BY a.airport_id, a.`airportname`, a.`bname`, a.bid ) A GROUP BY A.airportname

SELECT city.CID as pid, city.`name` as pname, GROUP_CONCAT(DISTINCT sat.airport_id) as aids, GROUP_CONCAT(distinct sat.airport_name) as anames FROM sys_city city inner join sys_airport sat on sat.province=city.CID inner JOIN sys_user_airport sua on sat.airport_id=sua.airport_id inner JOIN sys_users su on su.id=sua.sys_id
su.code=#{code}
and cid=#{cid}
group by city.CID,city.`name`
