update.if:
update sys_users set update_time=#{updatedatetime}

	,name=#{name}


	,password=#{password}


	,entryTime=#{entryTime}


	,state=#{state}


	,phone=#{phone}


	,code=#{code}


	,userType=#{userType}


	,login_name=#{loginName}

,sex=#{sex},dtflag=#{dtflag} where id=#{id}

mapper.select:
SELECT `id`, `name`, `phone`, `state`, `entry_time` as entryTime , `password`, `sex`, `photo`, `user_type` as userType, `login_name` as loginName, `create_time` as createdatetime, `update_time` as updatedatetime, `salt`, `dtflag`, `code`, `job`, `extra_passwd` FROM `sys_users` u
id=#{id}
and dtflag=#{dtflag}
and DATE_FORMAT(create_time,'%Y-%m')=#{dateTime}
