mapper.select:
SELECT role.*,GROUP_CONCAT(sa.id ) AS 'resources.id',GROUP_CONCAT(sa.`name` ) AS 'resources.name' FROM `sys_roles` role LEFT JOIN `sys_role_author` sau ON sau.`role_id`=role.`id` LEFT JOIN `sys_author` sa ON sa.`id`=sau.`author_id` where role.dtflag!=1
and role.id =#{id}
and role.name=#{notLikeName}
and role.name like CONCAT('%', #{name}, '%')
and role.description like CONCAT('%', #{description},'%')
GROUP BY role.name

mapper.update:
UPDATE sys_roles
SET
id=#{id}

	,dtflag=#{dtflag}


	,name = #{name}


	,description = #{description}


	,update_time = #{updatedatetime}


	,url=#{url}


	,priority=#{priority}


	,initPwd=#{initPwd}

WHERE id = #{id}
